sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || newValue == oldValue) {
      return;
   }

	var getEmailAddrFunc = new GlideAjax('GetEmailAddressAndAge');
	getEmailAddrFunc.addParam('sysparm_name', 'getEmail');
	getEmailAddrFunc.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	getEmailAddrFunc.getXML(populateEmailAddress);

	function populateEmailAddress(response) {
		var emailAddress = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email', emailAddress);
	}

   //Type appropriate comment here, and begin script below

}
