sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue == '') {
      return;
   }
	var whatneeded = g_form.getValue('u_what_needed');

	g_form.clearOptions('u_what_needed');

	if (newValue == 'hr') {
		g_form.addOption('u_what_needed', 'hr1', 'New Human Resource Option 1');
		g_form.addOption('u_what_needed', 'hr2', 'New Human Resource Option 2');
		g_form.addOption('u_what_needed', 'hr3', 'New Human Resource Option 3');
		g_form.addOption('u_what_needed', 'other', 'other...');

	} else if (newValue == 'facilities') {
		g_form.addOption('u_what_needed', 'f1', 'Facilities 1');
		g_form.addOption('u_what_needed', 'f2', 'Facilities 2');
		g_form.addOption('u_what_needed', 'f3', 'Facilities 3');
		g_form.addOption('u_what_needed', 'f4', 'Facilities 4');
		g_form.addOption('u_what_needed', 'f5', 'Facilities 5');
		g_form.addOption('u_what_needed', 'f6', 'Facilities 6');
	} else if (newValue == 'legal') {
		g_form.addOption('u_what_needed', 'l1', 'Legal 1');
		g_form.addOption('u_what_needed', 'l2', 'Legal 2');
		g_form.addOption('u_what_needed', 'l3', 'Legal 3');
	}

   if (isLoading && !(g_form.isNewRecord())) {
	   g_form.setValue('u_what_needed', whatneeded);
   }
}
