sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var rightNow = new GlideDateTime();
	var whenNeeded = new GlideDateTime(current.u_when_needed);

	gs.info("Comparing 2 days: " + rightNow.getValue() + " to " + whenNeeded.getValue() + " to ensure the needed date is after today!");

	if (whenNeeded.getDate().before(rightNow.getDate())) {
		gs.addErrorMessage("When Needed must not be set to before today, when creating a record");
		current.setAbortAction(true);
	}

	gs.info("Now check to see if it is today, or not...  If so, then 1 more abort message");

	var today = rightNow.getLocalDate();
	var whenNeededDate = whenNeeded.getLocalDate();
	if (whenNeededDate.compareTo(today) == 0) {
		gs.addErrorMessage("BCC For Inserts, the needed date must be at least 1 day in future!");
		current.setAbortAction(true);
	}





})(current, previous);
